package insaneColouredTriangles;

import org.junit.Test;
import static org.junit.Assert.assertEquals;
import org.junit.runners.JUnit4;

public class ExampleTests {

    @Test
    public void examples() {
     assertEquals('B', Kata.triangle("BRBRRRBGRGGGRBBBRRRRRGRBRGGGBRGRGGBGGRBGBGRGRRRGGGRBBGGRGRRGGBBRBRRGBRGRBRGBGGGRBRGBRGBRRGGGGBGBBBGGGGGRGBBGGGBBRRGRGGBRGRGBGBGGBBRGBRBRGGBBBBGBGBRGRRGGRBBRGRRGBRGGRBRGBGBBRBGBRBBGRGRBBGBGGRGGGGBGBGBBGRGRRR"));
      assertEquals('R', Kata.triangle("GB"));
      assertEquals('R', Kata.triangle("RRR"));
      assertEquals('B', Kata.triangle("RGBG"));
      assertEquals('G', Kata.triangle("RBRGBRB"));
      assertEquals('G', Kata.triangle("RBRGBRBGGRRRBGBBBGG"));
      assertEquals('B', Kata.triangle("BGGGGGGGGGRGGGGGBGGGGGGGGGGGGGGBGGGGGGGGGGGGGGBGGGGGGGGGGGGGGGGGGBGGGGGGGGGGGGGGBGGGGGGGGGGGGGGBGGGGGGGGGGGGGGBGGGGGGGGGGGGGGBGGGGGGGGGGGGGGBGGGGGGGGGGGGGGBGGGGGGGGGGGGGGBGGGGGGGGGGGGGGBGGGGGGGGGGGGGGBGGGGGGGGGGGGGGBGGGGGGGGGGGGGGBGGGGGGGGGGGGGG"));

    assertEquals('G', Kata.triangle("RBRGBRB"));
    }
    @Test
    public void time() {
    	for(int i =0; i<100; i++) {
    		assertEquals('B', Kata.triangle("RBRGBRBGGRRRBGBBBGGRBRGBRBGGRRRBGBBBGGRBRGBRBGGRRRBGBBBGG"
    				+ ""
    				+ ""
    				+ ""
    				+ ""
    				+ ""
    				+ ""
    				+ ""
    				+ ""));
    	}
    }
}